# Create a group mask
3dMean -mask_inter -prefix mask `ls /home/maullz/NIDM-Ex/BIDS_Data/RESULTS/SOFTWARE_COMPARISON/ds120/AFNI/LEVEL1/sub-*/sub*.results/mask_group*.HEAD`

# Simulations for FWE corrected cluster-size inference
# 0.621678 5.83585 16.1792 is the average of the 3dFWHMx -acf calculations applied
# to the blur_est.sub_xxx.1D files for the 16 subjects
3dClustSim -both -mask mask+tlrc -acf 0.765135 3.87751 13.6935 -prefix ClustSim 

3dclust -1Dformat -nosum -1dindex 4 -1tindex 4 -2thresh -3.836 3.836 -dxyz=1 -savemask Positive_clust_mask 1.01 17 /home/maullz/NIDM-Ex/BIDS_Data/RESULTS/SOFTWARE_COMPARISON/ds120/AFNI/LEVEL2/Group+tlrc.HEAD

# Masking t_stat 
   3dcalc -a 'Group+tlrc[4]' -b 'mask+tlrc' \
            -expr 'a*b' -prefix Group_f_stat_masked -datum float   

# Binarizing both cluster masks
   3dcalc -a 'Positive_clust_mask+tlrc' -expr 'ispositive(a-0.5)' \
            -prefix Positive_binary_clust_mask                 
           
   3dcalc -a Positive_binary_clust_mask+tlrc -b Group_f_stat_masked+tlrc \
            -expr 'a*b' -prefix Group_positive_f_stat_clustered -datum float   

# Convert to NIFTI for upload to Neurovault
  3dAFNItoNIFTI -prefix Group_f_stat Group+tlrc"[4]"
  3dAFNItoNIFTI Group_f_stat_masked+tlrc
  3dAFNItoNIFTI -prefix Positive_clustered_f_stat Group_positive_f_stat_clustered+tlrc
  3dAFNItoNIFTI mask+tlrc